INSERT INTO `tbl_review` (`review_id`, `review_type`, `review_title`, `description`, `photo`, `video_link`, `review_status`, `hit_count`, `post_by`, `created_date`) VALUES
(20, 1, 'Samsung Galaxy Note 9', '\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Release date:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">August 2018 </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Weight:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">205g </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Dimensions:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">162 x 76.4 x 9mm </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">OS:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Android 8.1 &nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Screen size:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">6.4-inch </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Resolution:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">2960×1440 </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">CPU:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">Exynos 9810 </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">RAM:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">6/8GB </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Storage:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">128/512GB (up to 1TB\r\nwith card) </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Battery:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">4,000mAh </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Rear camera:&nbsp;</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">12MP + 12MP </span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333\">&nbsp;<b><span style=\"border:none windowtext 1.0pt;mso-border-alt:\r\nnone windowtext 0in;padding:0in\">Front camera:&nbsp;</span></b>8MP</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333\">&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:12.0pt;text-align:justify;line-height:\r\n150%;background:white;vertical-align:baseline\"><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">The Samsung Galaxy\r\nNote 9 is one of the best phones we\'ve ever tested - and also one of the most\r\nexpensive.&nbsp;</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><b><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">Screen:</span></b><span style=\"font-family:&quot;Times New Roman&quot;,serif;\r\nmso-fareast-font-family:&quot;Times New Roman&quot;;color:#333333\">&nbsp;Let\'s get right\r\nto that screen: it\'s beautiful. Yes, it\'s a bit narrow if you\'re coming from\r\nthe Note 5 or similar, but the way it wraps around the frame, the smaller\r\nbezels and the impressive color reproduction and brightness make it a real\r\nfavourite.</span></p>\r\n\r\n<p class=\"MsoNormal\" style=\"margin-bottom:0in;margin-bottom:.0001pt;text-align:\r\njustify;line-height:150%;background:white;vertical-align:baseline\"><span style=\"font-family:&quot;Times New Roman&quot;,serif;mso-fareast-font-family:&quot;Times New Roman&quot;;\r\ncolor:#333333\">&nbsp;</span></p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify;line-height:150%;\r\nbackground:white;vertical-align:baseline\"><strong><span style=\"font-size:11.0pt;\r\nline-height:150%;color:#333333;border:none windowtext 1.0pt;mso-border-alt:\r\nnone windowtext 0in;padding:0in\">Battery life:</span></strong><span class=\"apple-converted-space\"><span style=\"font-size:11.0pt;line-height:150%;\r\ncolor:#333333\">&nbsp;</span></span><span style=\"font-size:11.0pt;line-height:\r\n150%;color:#333333\">With 4,000mAh under the hood, the Note 9 has one of the\r\nbiggest batteries Samsung has ever thrust into a device, meaning it can easily\r\nlast through the day (plus it has new cooling tech to help it last longer in\r\ngaming sessions, although we\'ve not seen<span class=\"apple-converted-space\">&nbsp;</span><em><span style=\"border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">much</span></em><span class=\"apple-converted-space\">&nbsp;</span>proof\r\nof that). You can also charge over wireless easily, and fast charging boots in\r\n17% battery in 15 mins.</span></p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify;line-height:150%;\r\nbackground:white;vertical-align:baseline\"><span style=\"font-size:11.0pt;\r\nline-height:150%;color:#333333\">&nbsp;</span></p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify;line-height:150%;\r\nbackground:white;vertical-align:baseline\"><strong><span style=\"font-size:11.0pt;\r\nline-height:150%;color:#333333;border:none windowtext 1.0pt;mso-border-alt:\r\nnone windowtext 0in;padding:0in\">Camera:</span></strong><span class=\"apple-converted-space\"><span style=\"font-size:11.0pt;line-height:150%;\r\ncolor:#333333\">&nbsp;</span></span><span style=\"font-size:11.0pt;line-height:\r\n150%;color:#333333\">The cameras on Samsung phones are part of the reason they\r\nrank so highly in our list - the sheer capability and low light performance is\r\nimpressive. Photos are definitely more on the colourful side, but there are\r\nmore AI smarts in the mix to help you get the right shot at the right time.</span></p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify;line-height:150%;\r\nbackground:white;vertical-align:baseline\"><span style=\"font-size:11.0pt;\r\nline-height:150%;color:#333333\">&nbsp;</span></p>\r\n\r\n<p style=\"margin:0in;margin-bottom:.0001pt;text-align:justify;line-height:150%;\r\nbackground:white;vertical-align:baseline\"><strong><span style=\"font-size:11.0pt;\r\nline-height:150%;color:#333333;border:none windowtext 1.0pt;mso-border-alt:\r\nnone windowtext 0in;padding:0in\">Mini verdict:</span></strong><span class=\"apple-converted-space\"><b><span style=\"font-size:11.0pt;line-height:150%;\r\ncolor:#333333;border:none windowtext 1.0pt;mso-border-alt:none windowtext 0in;\r\npadding:0in\">&nbsp;</span></b></span><span style=\"font-size:11.0pt;line-height:\r\n150%;color:#333333\">It\'s hard to wrap the Samsung Galaxy Note 9 into a mini\r\nverdict - it does so much and we\'ve not even touched on the new,\r\ncamera-remote-enabled Bluetooth S Pen. The expansive, quality screen, the\r\nbattery life and the camera all combine to make this a stunning smartphone... if\r\nyou can afford it</span></p>\r\n\r\n<!--[if gte mso 9]><xml>\r\n <o:OfficeDocumentSettings>\r\n  <o:AllowPNG/>\r\n </o:OfficeDocumentSettings>\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n  <w:View>Normal</w:View>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:T